package org.fiserv.model;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import java.time.LocalDateTime;@Entity@Getter@Setterpublic class Transaction {       @Id       @GeneratedValue       @Column(name="transaction_id")       private Long transactionId;       @Column(name="transaction_amount")       private String transactionAmount;       @Column(name="transaction_type")       private String transactionType;       @Column(name="time_Stamp")       private LocalDateTime timeStamp;       @ManyToOne       @JoinColumn(name="fk_account_id")       private SavingsAccount account;}