package org.fiserv.model;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;@Entity@Getter@Setter@Table(name="users")public class User{       @Id       @GeneratedValue       @Column(name="user_id")       private Long userId;       @Column(nullable = false)       private String username;       @Column(nullable = false)       private String password;       @OneToOne(cascade = CascadeType.ALL,mappedBy = "user")       private SavingsAccount savingsAccount;}